const fs = require("fs");
const path = require("path");

function say() {
    console.log("你好node.js");
}

let filePath = path.join(__dirname, "./01.txt");
fs.readFile(filePath, "utf8", function (err, data) {
    if (err) {
        throw new Error("读取文件失败！");
    }
    console.log(data);
})





exports.say = say;